package basicSamples;

public class OperatorsSample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=12,b=15;
		System.out.println("a+b="+(a+b));
		System.out.println("a*b="+(a*b));
		System.out.println("b-a="+(b-a));
		System.out.println("b/a="+(b/a));
		System.out.println("a%b="+(a%b));
		int h=a;
		h+=a;
		System.out.println("h"+h);
		
		

	}

}
